      // Si se cambia el animal, verificar que existe
      let animalNom = mockPart.animal_nom;
      if (partData.animal_id && partData.animal_id !== mockPart.animal_id) {
        const animal = mockAnimals.find(a => a.id === partData.animal_id);
        if (!animal) {
