    'borrar_usuarios',
    'cambiar_contrase√±as',
    'gestionar_explotaciones', 
    'ver_estadisticas',
    'exportar_datos'
  ],
  'editor': [
    'consultar', 
    'actualizar', 
    'ver_estadisticas'
  ],
